INSERT INTO CON_CONTA (CON_ID,CON_CD,CON_NM,CON_DS,CON_DS_BASE_NORMATIVA) VALUES (2023,'820.02','CUPOM DE MOEDA ESTRANGEIRO - DESCASAMENTO VERTICAL (DV)','Corresponde a 10% (dez por cento) do menor valor entre o valor absoluto da soma das exposicões ponderadas compradas e o valor absoluto da soma das exposicões ponderadas vendidas em cada vertice para cada moeda estrangeira.','BN: art. 7 da Circular 3.362/07');
INSERT INTO CON_CONTA (CON_ID,CON_CD,CON_NM,CON_DS,CON_DS_BASE_NORMATIVA) VALUES (2024,'820.03','CUPOM DE MOEDA ESTRANGEIRA - DESCASAMENTO HORIZONTAL DENTRO DA ZONA DE VENCIMENTO (DHZ)','Corresponde ao menor valor entre a soma das exposicões liquidas positivas e a soma dos valores absolutos das exposicões liquidas negativas de cada vertice pertencente à zona, multiplicada pelo fator W para cada moeda estrangeira.','BN: art. 8 da Circular 3.362/07');
INSERT INTO CON_CONTA (CON_ID,CON_CD,CON_NM,CON_DS,CON_DS_BASE_NORMATIVA) VALUES (2025,'820.04','CUPOM DE MOEDA ESTRANGEIRA - DESCASAMENTO HORIZONTAL ENTRE AS ZONAS DE VENCIMENTO (DHE)','Corresponde a soma dos seguintes valores: 40% (quarenta por cento) do menor valor absoluto entre as exposicões totais da zona 1 e zona 2, se tiverem exposicões totais contrarias; 40% (quarenta por cento) do menor valor absoluto entre as exposicões totais da zona 2 e da zona 3, se tiverem exposicões totais contrarias; e O valor das exposicões totais de cada zona corresponde ao somatorio das exposicões liquidas de cada vertice pertencente à zona, para cada moeda estrangeira.','BN: artigos 9 e 10 da Circular 3.362/07');
